import React from 'react';
import Webcam from 'react-webcam';
export function CameraComponent() {
    const webcamRef = React.useRef(null);
  
    // Function to handle when the camera stream is successfully loaded
    const handleCameraLoad = () => {
      // Access the camera stream and start displaying it
      navigator.mediaDevices.getUserMedia({ video: true })
        .then((stream) => {
          webcamRef.current.srcObject = stream;
        })
        .catch((error) => {
          console.log('Error accessing camera:', error);
        });
    };
  
    return <Webcam className="camera-container" audio={false} ref={webcamRef} onLoadedData={handleCameraLoad} />;
  }
