               , b_col:'red'
            }
    }
    add =()=>{
        {this.setState({count:this.state.count+1})}
        
    }
    b_color=()=>this.setState({col:'blue',b_col:'red'})
    
    render(){
        return(
            <div>
                <button onClick={this.add,this.b_color} style={{background:this.state.b_col}}>+{this.state.count}</button>
            </div>
        );
    }
}
