import React from "react";

class Test extends React.Component {
	constructor() {
		super();
		this.state = {
			counter: 0,
			colour1: "",
			colour2: "",
		};
	}

	increment = () => {
		this.setState({
			counter: this.state.counter + 1,colour1: "blue",colour2: "white",
		});
	};

	decrement = () => {
		this.setState({counter: this.state.counter - 1,colour1: "white",colour2: "green",});
	};

	render() {
		return (
			<div>
				<button	onClick={this.increment}style={{ backgroundColor: this.state.colour1 }}>+</button>
				<p>{this.state.counter}</p>
				<button	onClick={this.decrement}style={{ backgroundColor: this.state.colour2 }}>-</button>
			</div>
		);
	}
}

export default Test;
