import React from "react";
class Ca extends React.Component ()
{
    constructor(){
        super()
        this.state={count:0
               , b_col:'red'
            }
    }
    add =()=>{this.setState({count:this.state.count+1,b_col:'blue'})}
    // b_color=()=>this.setState({col:'blue',b_col:'red'})
    
    render(){
        return(
            <div>
                <button onClick={this.add} style={{background:this.state.b_col}}>+{this.state.count}</button>
            </div>
        );
    }
}
export default Ca;
