import React from "react";

class Test extends React.Component {
	constructor() {
		super();
		this.state = {
			counter: 0,
			col1: "",
			col2: "",
		};
	}

	increment=()=>{this.setState({counter: this.state.counter + 1,col1: "blue",col2: "white",})};

	decrement=()=>{this.setState({counter: this.state.counter - 1,col1: "white",col2: "green",})};

	render() {
		return (
			<div>
				<button	onClick={this.increment}style={{ backgroundColor: this.state.col1 }}>+</button>
				<p>{this.state.counter}</p>
				<button	onClick={this.decrement}style={{ backgroundColor: this.state.col2 }}>-</button>
			</div>
		);
	}
}

export default Test;
