import { Component } from "react";

export class MyForm extends Component {

    constructor(props) {
        console.log("hello");
        super(props);
        this.state = {
            val1: '', error1: '',
            val2: '', error2: '',
            val3: '', error3: ''
        }
    }


    trackChange1 = (e) => {
        // var c =  e.target.value.length;
        var c1 = e.target.value.slice(-1);
        console.log(c1);
        if(c1 >= '0' && c1 <= '9') {
            this.setState({error1: 'Blog title should not begin with number'});    
        }
        else {
            this.setState({val1: e.target.value});    
            this.setState({error1: ''});
        }        
    }

    trackChange2 = (e) => {
        var c1 =  e.target.value;
        if(c1.indexOf('@') === -1) {
            this.setState({val2: e.target.value});    
            this.setState({error2: ''});            
        }
        else {
            this.setState({error2: 'Blog content should not contain any @'});
        }
    }

    trackChange3 = (e) => {
        this.setState({val3: e.target.value});
    }

    render() {
        return (
            <div>
                <h1>Blog</h1>
                <form action="#" method="post">
                    <label for="">Blog Title : </label><br />
                    <label><input type="text" name="blog_title" onChange={this.trackChange1} value={this.state.val1}></input></label>
                    <br /><br />
                    <label for="">Blog Content : </label><br />
                    <label><textarea row="5" col ="50" name="blog_content" onChange={this.trackChange2} value={this.state.val2}></textarea></label>
                    <br /><br />
                    <label>Author</label><br />
                    <label>
                        <select onChange={this.trackChange3}>
                            <option value="anish">Anish</option>
                            <option value="suhail">Shushant</option>
                            <option value="suhail">Aditya</option>
                            <option value="shubham">Shubham</option>
                        </select>
                    </label>
                    <br /><br />
                    <label><button type="submit">Submit</button></label>
                </form>
                <p>{this.state.error1}</p>
                <p>{this.state.error2}</p>
                <p>{this.state.error3}</p>
            </div>
        );
    }
}
